mapper.select:
SELECT r.id,r.pNo,r.rRegDate,r.start_time,r.end_time,r.totalMoney FROM reservation r inner join product p on r.pNo = p.pNo
${searchCondition} Like '%${searchKeyword}%'
ORDER BY r.rRegDate LIMIT #{startBoard}, #{cnt}


  select p.pNo, (select count(r.rNo) from reservation r where year(r.rental)=${year} and month(r.rental)=${month} and r.pNo = p.pNo ) as reservationCount
  from product p
  order by p.pNo ASC;
